example.code:
using System;
using Gtk;

class StatusbarSample
{
	Statusbar sb;
	const int id = 1;
	int count;

	static void Main ()
	{
		new StatusbarSample ();
	}

	StatusbarSample ()
	{
		Application.Init ();
		count = 0;
		Window win = new Window ("StatusbarSample");
		win.DeleteEvent += new DeleteEventHandler (OnWinDelete);
		win.SetDefaultSize (150, 100);

		VBox vbox = new VBox (false, 1);
		win.Add (vbox);

		Button btn = new Button ("Add to counter");
		btn.Clicked += new EventHandler (OnButtonClicked);
		vbox.Add (btn);

		sb = new Statusbar ();
		sb.Push (id, "Welcome!");
		sb.HasResizeGrip = false;
		vbox.Add (sb);

		win.ShowAll ();
		Application.Run ();
	}

	void OnButtonClicked (object obj, EventArgs args)
	{
		count ++;
		string message = String.Format ("Pushed {0} times", count);
		sb.Pop (id);
		sb.Push (id, message);
	}

	void OnWinDelete (object obj, DeleteEventArgs args)
	{
		Application.Quit ();
	}
}
