Docs.summary:
An HBox is a specific type of  for packing widgets horizontally.



using System;
using Gtk;

class HBoxTester {

     static void Main ()
     {
          Application.Init ();
          Window myWindow = new Window ("HBox Widget");

          HBox myBox = new HBox (false, 4);

          //Add some buttons to the horizontal box
          AddButton (myBox);
          AddButton (myBox);

          //Add the box to a Window container
          myWindow.Add (myBox);
          myWindow.ShowAll ();
          Application.Run ();
     }

     static void AddButton (HBox box)
     {
          box.PackStart (new Button ("Button"), true, false, 0);
     }     
}
